import React from "react";
import { Form, FormGroup, Label, Input, Col } from "reactstrap";
import axios from "../Utils/axios";
import history from "../components/history";
import Redirect from "../hoc/redirect";
class PostFormOfBook extends React.Component {
    state = {
        firstName: '',
        lastName: '',
        email: '',
        password: '',
    };
    handleChange = event => {
        this.setState({ [event.target.name]: event.target.value });
        console.log(this.state);
    };
    handleSubmit = event => {
        event.preventDefault();
        const userDetail = {
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            email: this.state.email,
            password: this.state.password
        };
        axios.post("/user",
            {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem("access_token")}`
                },
                data: userDetail,
            }
        )
            .then(res => {
                console.log(res);
                /*this.props.history.push("/");*/
                history.replace("/")
            })
            .catch(error => {
                /* this.setState({error: error.response.data.detail});*/
                console.log(JSON.stringify(error.response.data))

            });
    };

    render() {
        return (
            <div className="container">
                <div className="col-md-10 offset-md-1">
                    <Form
                        onSubmit={this.handleSubmit}
                        className="my-4 border border-light p-5 shadow-lg form-background"
                    >
                        <h2 className="text-center mb-5">USER DETAIL FORM</h2>
                        <FormGroup row>
                            <Label for="firstName" sm={2}>
                                <h5>First name: </h5>
                            </Label>
                            <Col sm={10}>
                                <Input
                                    type="text"
                                    name="firstName"
                                    id="firstName"
                                    placeholder="Enter your first name."
                                    onChange={this.handleChange}
                                />
                            </Col>
                        </FormGroup>
                        <FormGroup row>
                            <Label for="lastName" sm={2}>
                                <h5>Last name: </h5>
                            </Label>
                            <Col sm={10}>
                                <Input
                                    type="text"
                                    name="lastName"
                                    id="lastName"
                                    placeholder="Enter your last name."
                                    onChange={this.handleChange}
                                />
                            </Col>
                        </FormGroup>

                        <FormGroup row>
                            <Label for="email" sm={2}>
                                <h5>email: </h5>
                            </Label>
                            <Col sm={10}>
                                <Input
                                    type="email"
                                    name="email"
                                    id="email"
                                    placeholder="Enter your email"
                                    onChange={this.handleChange}
                                />
                            </Col>
                        </FormGroup>
                        <FormGroup row>
                            <Label for="examplePassword" sm={2}>
                                <h5>Password: </h5>
                            </Label>
                            <Col sm={10}>
                                <Input
                                    type="password"
                                    name="password"
                                    id="examplePassword"
                                    placeholder="Enter password"
                                    onChange={this.handleChange}
                                />
                            </Col>
                        </FormGroup>
                        <FormGroup row>
                            <button className="btn btn-outline-success btn-md rounded-0 mx-auto px-5 shadow">
                                <h2>SAVE</h2>
                            </button>
                        </FormGroup>
                    </Form>
                </div>
            </div>
        );
    }
}
export default Redirect(PostFormOfBook);
