import React from "react";
import { Form, FormGroup, Label, Col } from "reactstrap";
import axios from "../Utils/axios";
import { navigate } from "@reach/router";
import {
    UncontrolledButtonDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem
} from "reactstrap";
class UpdateBookIssue extends React.Component {
    state = {
        bookIssue:[],
        books: [],
        users: [],
        user:"",
        book:[],
        selectedUser:"Select the user",
        selectedBook: "Select the book"
    };

    handleUserChange = event => {
        let user=this.state.users.find(obj=>obj.id==event.target.value)





    };
    handleBookChange = event => {
        let selectedbook=this.state.books.find(obj=>obj.id==event.target.value)
        console.log(selectedbook)
        this.setState({ book: selectedbook });
        console.log("state book"+this.state.book)
    };
    handleSubmit = event => {
        event.preventDefault();
        const bookIssue={
            user: this.state.users.find(obj=>obj.id==this.state.user),
            book : this.state.books.find(obj=>obj.id==this.state.book)
        };
        axios({
            method: "post",
            url: "/bookIssue",
            data: bookIssue
        })
            .then(res => {
                console.log(res);
                /*this.props.history.push("/");*/
                navigate("/bookIssue");
            })
            .catch(error => {
                /* this.setState({error: error.response.data.detail});*/
                console.log(error);
            });
    };
    componentWillMount() {
        this.getUserList();
        this.getBookList();

        axios.get("/bookIssue/"+this.props.id).then(data => {
            this.setState({
                bookIssue: data.data
            });
            console.log("data data"+ data.data.id);

        });

    }
    getUserList = () => {
        let users;
        axios.get("/user").then(data => {
            console.log(data);
            users = data.data;
            this.setState({
                users: users
            });

        });
    };
    getBookList = () => {
        let books;
        axios.get("/book").then(data => {
            console.log(data);
            books = data.data;
            this.setState({
                books: books
            });
            console.log(this.state.books);
        });
    };
    render() {
        return (
            <div className="container">
                <div className="col-md-10 offset-md-1">
                    <Form
                        onSubmit={this.handleSubmit}
                        className="my-4 border border-light p-5 shadow-lg form-background w-75 mx-auto"
                    >
                        <h2 className="text-center mb-5">BOOK ISSUE</h2>
                        <FormGroup row>
                            <Label for="firstName" sm={4} className="text-center">
                                <h5>Book: </h5>
                            </Label>
                            <Col sm={4}>
                                <UncontrolledButtonDropdown>
                                    <DropdownToggle caret>Select the book</DropdownToggle>
                                    <DropdownMenu onClick={this.handleBookChange} value={this.state.bookIssue.book}>
                                        {this.state.books.map((book, key)=>(
                                            <DropdownItem key={key} value={book.id}>{book.name}</DropdownItem>
                                        ))}
                                    </DropdownMenu>
                                </UncontrolledButtonDropdown>
                            </Col>
                        </FormGroup>
                        <FormGroup row>
                            <Label for="User" sm={4} className="text-center">
                                <h5>User: </h5>
                            </Label>
                            <Col sm={4}>
                                <UncontrolledButtonDropdown >
                                    <DropdownToggle caret>{this.state.user}</DropdownToggle>
                                    <DropdownMenu onClick={this.handleUserChange}>
                                        {this.state.users.map((user, key)=>(
                                            <DropdownItem key={key} value={user.id}  >{user.firstName} {user.lastName}</DropdownItem>
                                        ))}

                                    </DropdownMenu>
                                </UncontrolledButtonDropdown>

                            </Col>
                        </FormGroup>
                        <FormGroup row>
                            <button className="btn btn-outline-success btn-md rounded-0 mx-auto px-5 shadow">
                                <h2>SAVE</h2>
                            </button>
                        </FormGroup>
                    </Form>
                </div>
            </div>
        );
    }
}
export default UpdateBookIssue;
