import React from 'react'
import Auth from "../service/auth0";
import UserList from "../components/UserList"
import {navigate} from "@reach/router";

const auth = new Auth();

const redirect=(WrappedComponent)=>{
    function homeRedirect() {
        navigate("/")
    }

    return (props) => {

        return (
            WrappedComponent.name==='CustomNavbar'|| WrappedComponent.name==='Callback'|| WrappedComponent.name==='App'?<WrappedComponent auth={auth} {...props}/>:
            auth.isAuthenticated()?<WrappedComponent auth={auth}/>:homeRedirect()
        )
    }
};
export default redirect
