import React from "react";
import { Form, FormGroup, Label, Input, Col } from "reactstrap";
import axios from "../Utils/axios";
import {navigate} from "@reach/router";

class UpdateBook extends React.Component {
    state = {
        id:0,
        firstName:"",
        lastName:"",
        email:"",
        error: [],
        user:[]
    };
    handleChange = event => {
        this.setState({ [event.target.name]: event.target.value });
        console.log(this.state);
    };
    handleSubmit = event => {
        event.preventDefault();
        const user = {
            id: this.state.id,
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            email: this.state.email
        };
        console.log("check id"+user);
        axios({
            method: "put",
            url: "/user",
            data: user
        })
            .then(res => {
                console.log(res);
                navigate("/");
            })
            .catch(error => {
                this.setState({ error: error.response.data.detail })

            });
    };
    componentDidMount() {
        axios.get("/user" + this.props.id)
            .then(res => {
                console.log("userList"+res);
                this.setState({id: res.data.id, firstName: res.data.firstName, lastName:res.data.lastName,
                    email:res.data.email})

            });
    }

    render() {
        return (
            <div className="container">
                <div className="col-md-10 offset-md-1">
                    <Form
                        onSubmit={this.handleSubmit}
                        className="my-4 border border-light p-5 shadow-lg form-background"
                    >
                        <h2 className="text-center mb-5">UPDATE BOOK FORM</h2>
                        <FormGroup row>

                            <Label for="exampleEmail" sm={2}>
                                <h5>First name: </h5>
                            </Label>
                            <Col sm={10}>
                                <Input
                                    type="text"
                                    name="firstName"
                                    id="exampleEmail"
                                    placeholder="Enter first name."
                                    defaultValue={this.state.firstName}
                                    onChange={this.handleChange}
                                />
                            </Col>
                        </FormGroup>
                        <FormGroup row>
                            <Label for="examplePassword" sm={2}>
                                <h5>Last name: </h5>
                            </Label>
                            <Col sm={10}>
                                <Input
                                    type="text"
                                    name="lastName"
                                    defaultValue={this.state.lastName}
                                    id="examplePassword"
                                    placeholder="Enter last name"
                                    onChange={this.handleChange}
                                />
                            </Col>
                        </FormGroup>
                        <FormGroup row>
                            <Label sm={2}>
                                <h5>Email: </h5>
                            </Label>
                            <Col sm={10}>
                                <Input
                                    type="text"
                                    name="email"
                                    id="examplePassword"
                                    defaultValue={this.state.email}
                                    placeholder="Book ISBN"
                                    onChange={this.handleChange}
                                />
                            </Col>
                        </FormGroup>
                        <FormGroup row>
                            <button className="btn btn-outline-success btn-md rounded-0 mx-auto px-5 shadow">
                                <h2>UPDATE</h2>
                            </button>
                        </FormGroup>
                    </Form>
                </div>
            </div>
        );
    }
}
export default UpdateBook;
