import React from "react";
import { Table } from "reactstrap";
import axios from "../Utils/axios";
import "font-awesome/css/font-awesome.css";
import ConfirmBox from "../components/ConfirmBox";
import { Link } from "@reach/router";
class BookList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      books: []
    };
    this.delete = this.delete.bind(this);
  }
  componentDidMount() {
    if((this.props.search&&this.props.keyword)==null){
      console.log("without keywor")
        this.getBookList();
    }else{
      console.log("withkeyword");

        axios({method:"get", url:"/book/name/"+this.props.keyword }).then(data=>{
            let boo=data.data;
            console.log(boo)
          this.setState({
              books:boo
          });
            console.log(this.state.books)
        })
    }
  }
  getBookList = () => {
    let books;
    axios.get("/book").then(data => {
      console.log(data);
      books = data.data;
      this.setState({
        books: books
      });
      console.log(this.state.books);
    });
  };
  search=(search, keyword)=>{

    console.log(keyword);
    axios({method:"get", url:"/book/name/"+keyword }).then(data=>{

  const book={
    id:data.data.id,
      name:data.data.name,
      quantity:data.data.quantity,
      location: data.data.location,
      description: data.data.description,
      isbn: data.data.isbn,
      publisher: data.data.publisher,
      author: data.data.author
  };
        console.log("shrestha"+data)
      /*this.setState({
          books: JSON.stringify(data).data
      })*/
    });
      console.log("suvha"+this.state.books)
  };
  delete(id) {
    axios.delete("/book/" + id).then(() => {
      this.getBookList();
    });
  }
  render() {
    return (
      <div className="container-fluid">
          <Link to="/create"><button className="btn btn-outline-info rounded-0 mt-2 shadow">Add Book</button></Link>
        <Table bordered className="table-hover my-3">
          <thead>
            <tr className="table-info">
              <th>Sno</th>
              <th>Name</th>
              <th>ISBN</th>
              <th>Author</th>
              <th>Publisher</th>
              <th>Description</th>
              <th>Quantity</th>
              <th>Location</th>
              <th>Delete</th>
              <th>Edit</th>
            </tr>
          </thead>
          <tbody>
            {this.state.books.map(book => (
              <tr key={book.id}>
                <th scope="row" key={book.id}>
                  {book.id}
                </th>
                <td>{book.name}</td>
                <td>{book.isbn}</td>
                <td>{book.author}</td>
                <td>{book.publisher}</td>
                <td>{book.description}</td>
                <td>{book.quantity}</td>
                <td>{book.location}</td>
                <td>
                  <ConfirmBox id={book.id} delete={this.delete} />
                </td>
                <td>
                  <Link to={`/update/${book.id}`}
                    className="btn btn-primary"
                  >
                    <i className="fas fa-edit" />
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </div>
    );
  }
}
export default BookList;
