import React from "react";
import { Form, FormGroup, Label, Input, Col } from "reactstrap";
import axios from "../Utils/axios";
import { navigate } from "@reach/router";
import { Dropdown, DropdownToggle, DropdownMenu, DropdownItem } from 'reactstrap';
class PostFormOfBook extends React.Component {
    state = {
      books:[],
        users:[],
        open: false
    };
    toggle=()=>{
        this.setState({
            open: !this.state.open
    });
    };
    handleChange = event => {
        this.setState({ [event.target.name]: event.target.value });
        console.log(this.state);
    };
    handleSubmit = event => {
        event.preventDefault();
        const userDetail = {
            firstName: this.state.firstName,
            lastName: this.state.lastName,
            email: this.state.email,
            password: this.state.password
        };
        axios({
            method: "post",
            url: "/user",
            data: userDetail
        })
            .then(res => {
                console.log(res);
                /*this.props.history.push("/");*/
                navigate("/");
            })
            .catch(error => {
                /* this.setState({error: error.response.data.detail});*/
                console.log(error)

            });
    };
    componentDidMount(){
        this.getUserList();
        this.getBookList()
    }
    getUserList = () => {
        let users;
        axios.get("/user").then(data => {
            console.log(data);
            users = data.data;
            this.setState({
                users: users
            });
            console.log(this.state.books);
        });
    };
    getBookList = () => {
        let books;
        axios.get("/book").then(data => {
            console.log(data);
            books = data.data;
            this.setState({
                books: books
            });
            console.log(this.state.books);
        });
    };
    render() {
        return (
            <div className="container">
                <div className="col-md-10 offset-md-1">
                    <Form
                        onSubmit={this.handleSubmit}
                        className="my-4 border border-light p-5 shadow-lg form-background w-75"
                    >
                        <h2 className="text-center mb-5">BOOK ISSUE</h2>
                        <FormGroup row >
                            <Label for="firstName" sm={6} className="mx-auto">
                                <h5>First name: </h5>
                            </Label>
                            <Col sm={6} className="mx-auto">
                                <Dropdown isOpen={this.state.open} toggle={this.toggle}>
                                    <DropdownToggle caret>
                                        Dropdown
                                    </DropdownToggle>
                                    <DropdownMenu>
                                        <DropdownItem header>Header</DropdownItem>
                                        <DropdownItem disabled>Action</DropdownItem>
                                        <DropdownItem>Another Action</DropdownItem>
                                        <DropdownItem divider />
                                        <DropdownItem>Another Action</DropdownItem>
                                    </DropdownMenu>
                                </Dropdown>
                            </Col>
                        </FormGroup>
                        <FormGroup row>
                            <button className="btn btn-outline-success btn-md rounded-0 mx-auto px-5 shadow">
                                <h2>SAVE</h2>
                            </button>
                        </FormGroup>
                    </Form>
                </div>
            </div>
        );
    }
}
export default PostFormOfBook;
