import React from "react";
import { Table } from "reactstrap";
import axios from "axios";
class CustomNavbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      books: []
    };
  }
  componentDidMount(){
      this.search()
  }
  search = () => {
    let books;
    axios.get("http://localhost:8080/api/V1/book").then(data=> {
      books = data;
      this.setState({
        books: books
      });
    });
  };
  render() {
    return (
      <div className="container">
        <Table bordered className="table-hover my-5">
          <thead>
            <tr className="table-info">
              <th>Sno</th>
              <th>Name</th>
              <th>ISBN</th>
              <th>Author</th>
              <th>Publisher</th>
              <th>Description</th>
              <th>Quantity</th>
              <th>Location</th>
            </tr>
          </thead>
          <tbody>
            {this.state.books.map(book => {
              return (
                <tr>
                  <th scope="row">{book.id}</th>
                  <td>{book.name}</td>
                  <td>{book.isbn}</td>
                  <td>{book.author}</td>
                  <td>{book.publisher}</td>
                  <td>{book.description}</td>
                  <td>{book.quantity}</td>
                  <td>{book.location}</td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </div>
    );
  }
}
export default CustomNavbar;
