import React from "react";
import { Table } from "reactstrap";
import axios from "../Utils/axios";
import "font-awesome/css/font-awesome.css";
import ConfirmBox from "../components/ConfirmBox";
import {Link} from "@reach/router";
class BookList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
        error:''
    };
    this.delete = this.delete.bind(this);
  }
  componentDidMount() {
      if ((this.props.search && this.props.keyword) == null) {
          console.log("without keywor");
          this.getBookList();
      } else {
          console.log("withkeyword");
          this.checkSearchType(this.props.search,this.props.keyword)

      }
  }
  checkSearchType=(search, keyword)=>{
    if(search==="ISBN"){
      this.searchByIsbn(keyword);
    }else{
      this.searchByBookName(keyword);
    }
  };
  getBookList = () => {
    let books;
    axios.get("/book").then(data => {
      console.log(data);
      books = data.data;
      this.setState({
        books: books
      });
      console.log(this.state.books);
    });
  };


  render() {
    return (
      <div className="container-fluid">
        <Link to="/create">
          <button className="btn btn-outline-info rounded-0 mt-2 shadow">
            Add Book
          </button>
        </Link>
          <Link to="/user">
              <button className="btn btn-outline-info rounded-0 mt-2 shadow ml-2">
                 UserList
              </button>
          </Link>
          <div>{(this.state.error)?<h4 className="text-danger text-center">{this.state.error}</h4>:
              <Table bordered className="table-hover my-3">
                  <thead>
                  <tr className="table-info">
                      <th>Sno</th>
                      <th>Name</th>
                      <th>ISBN</th>
                      <th>Author</th>
                      <th>Publisher</th>
                      <th>Description</th>
                      <th>Quantity</th>
                      <th>Location</th>
                      <th>Delete</th>
                      <th>Edit</th>
                  </tr>
                  </thead>
                  <tbody>
                  {this.state.books.map(book => (
                      <tr key={book.id}>
                          <th scope="row" key={book.id}>
                              {book.id}
                          </th>
                          <td>{book.name}</td>
                          <td>{book.isbn}</td>
                          <td>{book.author}</td>
                          <td>{book.publisher}</td>
                          <td>{book.description}</td>
                          <td>{book.quantity}</td>
                          <td>{book.location}</td>
                          <td>
                              <ConfirmBox id={book.id} delete={this.delete} />
                          </td>
                          <td>
                              <Link to={`/update/${book.id}`} className="btn btn-primary">
                                  <i className="fas fa-edit" />
                              </Link>
                          </td>
                      </tr>
                  ))}
                  </tbody>

              </Table>
          }</div>

      </div>
    );
  }
}
export default BookList;
