import React, { Component } from "react";
/*import { BrowserRouter as Router, Route, Switch } from "react-router-dom";*/
import { Router } from "@reach/router";
import PostFormOfBook from "./components/PostFormOfBook";
import "./App.css";
import BookList from "./components/BookList";
import UserDetail from "./components/UserDetail";
import UpdateBook from "./components/UpdateBook";
import UserList from "./components/UserList";
import EditUser from "./components/EditUser";
import CreateBookIssue from "./components/CreateBookIssue";
import BookIssueList from "./components/BookIssueList";
import UpdateBookIssue from "./components/UpdateBookIssue";
import Home from "./components/home";
import Callback from "./components/Callback";
import history from "./components/history";

const handleAuthentication = (nextState, replace) => {
    if (/access_token|id_token|error/.test(nextState.location.hash)) {
        this.props.auth.handleAuthentication();
    }
};
class App extends Component {
  render() {
    return (
      <Router history={history}>
          <Home path="/"/>
        <BookList path="/book" />
        <BookList path="/book/:search/:keyword" />
        <PostFormOfBook path="/book/create" />
        <UpdateBook path="/book/update/:id" />
        <UserDetail path="/user/create" />
        <UserList path="/user" />
        <EditUser path="/user/update/:id" />
        <CreateBookIssue path="/bookIssue/create" />
        <BookIssueList path="/bookIssue" />
        <UpdateBookIssue path="/bookIssue/update/:id" />
          <Route path="/callback" render={(props) => {
              handleAuthentication(props);
              return <Callback {...props} />
          }}/>
      </Router>
    );
  }
}

export default App;
