import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
class ConfirmBox extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            modal: false
        };

        this.toggle = this.toggle.bind(this);
    }

    toggle() {
        this.setState({
            modal: !this.state.modal
        });
    }
    render(){
return(
    <div>
        <Button onClick={this.toggle} className="btn btn-danger"><i className="fas fa-trash" /></Button>
        <Modal isOpen={this.state.modal} toggle={this.toggle} className="rounded-0">
            <ModalBody>
                <div className="text-danger fa-3x text-center"> <i className="far fa-times-circle"/></div>
                <h3 className="text-center">Are you sure ?</h3>
                <p className="text-center">Do you really want to delete this record? </p>
            </ModalBody>
                <div className="row">
                    <div className="col-md-12">
                <Button color="danger" className="float-right mb-2 mr-2 rounded-0" onClick={this.toggle}>Do Something</Button>

                <Button color="secondary" className="float-right  mb-2 mr-2 rounded-0" onClick={this.toggle}>Cancel</Button>
                    </div>
                </div>

        </Modal>
        <div className="modal" tabIndex="-1" role="dialog">
            <div className="modal-dialog" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <h5 className="modal-title">Modal title</h5>
                        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                            <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <div className="modal-body">
                        <p>Modal body text goes here.</p>
                    </div>
                    <div className="modal-footer">
                        <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                        <button type="button" className="btn btn-primary">Save changes</button>
                    </div>
                </div>
            </div>
        </div>
    </div>
);
    }
}
export default ConfirmBox;
