import React from "react";
import { Table } from "reactstrap";
import axios from "axios";
import "font-awesome/css/font-awesome.min.css";
class CustomNavbar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      books: []
    };
  }
  componentDidMount() {
    this.search();
  }
  search = () => {
    let books;
    axios.get("http://localhost:8080/api/V1/book").then(data => {
      console.log(data);
      books = data.data;
      this.setState({
        books: books
      });
      console.log(this.state.books);
    });
  };
  render() {
    return (
      <div className="container-fluid">
        <Table bordered className="table-hover my-5">
          <thead>
            <tr className="table-info">
              <th>Sno</th>
              <th>Name</th>
              <th>ISBN</th>
              <th>Author</th>
              <th>Publisher</th>
              <th>Description</th>
              <th>Quantity</th>
              <th>Location</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {this.state.books.map(book => {
              return (
                <tr>
                  <th scope="row" key={book.id}>
                    {book.id}
                  </th>
                  <td key={book.id}>{book.name}</td>
                  <td key={book.id}>{book.isbn}</td>
                  <td key={book.id}>{book.author}</td>
                  <td key={book.id}>{book.publisher}</td>
                  <td key={book.id}>{book.description}</td>
                  <td key={book.id}>{book.quantity}</td>
                  <td key={book.id}>{book.location}</td>
                  <td>
                      <button className="btn btn-danger"><i className="fas fa-trash" /></button>
                    <button className="btn btn-info ">
                      <i className="fas fa-edit" />
                    </button>

                  </td>
                </tr>
              );
            })}
          </tbody>
        </Table>
      </div>
    );
  }
}
export default CustomNavbar;
