import React, { Component } from 'react';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';
import axios from 'axios';


class PostFormOfBook extends React.Component{
    state={
        name:"",
        quantity:0,
        location:"",
        description:"",
        isbn:"",
        publisher:"",
        author:""
    };
    handleChange=event=> {
        this.setState({[event.target.name]: event.target.value});
    };
    handleSubmit=event=>{
        event.preventDefault();
        const book={
            name: this.state.name,
            quantity: this.state.quantity,
            location:  this.state.location,
            description: this.state.description,
            isbn: this.state.isbn,
            publisher: this.state.publisher,
            author: this.state.author
        };
        axios.post('http://localhost:8080/api/V1/book',{headers:{}},{book})
            .then(res=>{
                console.log(res);
                console.log(res.data)
            });
    };


    render() {
        return (
            <div className="container">
            <Form onSubmit={this.handleSubmit}>
                <FormGroup>
                    <Label for="exampleEmail">Book name: </Label>
                    <Input type="text" name="name" id="exampleEmail" placeholder="Book Name" onChange={this.handleChange} />
                </FormGroup>
                <FormGroup>
                    <Label for="examplePassword">Book location</Label>
                    <Input type="text" name="location" id="examplePassword" placeholder="Book price" onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup>
                    <Label for="examplePassword">Book ISBN</Label>
                    <Input type="text" name="isbn" id="examplePassword" placeholder="Book ISBN" onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup>
                    <Label for="examplePassword">Book quantity</Label>
                    <Input type="number" name="quantity" id="examplePassword" placeholder="Book quantity" onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup>
                    <Label for="examplePassword">Book publisher</Label>
                    <Input type="text" name="publisher" id="examplePassword" placeholder="Book publisher" onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup>
                    <Label for="examplePassword">Book author</Label>
                    <Input type="text" name="author" id="examplePassword" placeholder="Book author" onChange={this.handleChange}/>
                </FormGroup>
                <FormGroup>
                    <Label for="exampleText">Book description</Label>
                    <Input type="description" name="text" id="exampleText" onChange={this.handleChange}/>
                </FormGroup>


                <Button>Submit</Button>
            </Form>
            </div>
        );
    }
}
export default PostFormOfBook;
