import React from "react";
import { Form, FormGroup, Label, Col, Input } from "reactstrap";
import axios from "../Utils/axios";
import { navigate } from "@reach/router";
import {
  UncontrolledButtonDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from "reactstrap";
class UpdateBookIssue extends React.Component {
  state = {
    bookIssue: [],
    books: [],
    users: [],
    user: [],
    book: [],
    selectedUser: "Select the user",
    selectedBook: "Select the book"
  };

  handleUserChange = event => {
    let selectedUser = this.state.users.find(
      obj => obj.id == event.target.value
    );
    this.setState({
      selectedUser: selectedUser.firstName + " " + selectedUser.lastName,
      user: selectedUser
    });
  };
  handleBookChange = event => {
    let selectedbook = this.state.books.find(
      obj => obj.id == event.target.value
    );
    this.setState({
      book: selectedbook,
      selectedBook: selectedbook.name
    });
  };
  handleSubmit = event => {
    event.preventDefault();
    const bookIssue = {
      id: this.state.bookIssue.id,
      book: this.state.book,
      user: this.state.user
    };

    axios({
      method: "put",
      url: "/bookIssue",
      data: bookIssue
    })
      .then(res => {
        console.log(res);
        /*this.props.history.push("/");*/
        navigate("/bookIssue");
      })
      .catch(error => {
        /* this.setState({error: error.response.data.detail});*/
        console.log(error);
      });
  };
  componentWillMount() {
    this.getUserList();
    this.getBookList();

    axios.get("/bookIssue/" + this.props.id).then(data => {
      this.setState({
        bookIssue: data.data
      });
    });
  }
  getUserList = () => {
    let users;
    axios.get("/user").then(data => {
      console.log(data);
      users = data.data;
      this.setState({
        users: users
      });
    });
  };
  getBookList = () => {
    let books;
    axios.get("/book").then(data => {
      console.log(data);
      books = data.data;
      this.setState({
        books: books
      });
      console.log(this.state.books);
    });
  };
  render() {
    return (
      <div className="container">
        <div className="col-md-10 offset-md-1">
          <Form
            onSubmit={this.handleSubmit}
            className="my-4 border border-light p-5 shadow-lg form-background w-75 mx-auto"
          >
            <h2 className="text-center mb-5">BOOK ISSUE</h2>
            <FormGroup row>
              <Label for="firstName" sm={4} className="text-center">
                <h5>Book: </h5>
              </Label>
              <Col sm={4}>
                <UncontrolledButtonDropdown>
                  <DropdownToggle caret>
                    {this.state.selectedBook}
                  </DropdownToggle>
                  <DropdownMenu
                    onClick={this.handleBookChange}
                    value={this.state.bookIssue.book}
                  >
                    {this.state.books.map((book, key) => (
                      <DropdownItem key={key} value={book.id}>
                        {book.name}
                      </DropdownItem>
                    ))}
                  </DropdownMenu>
                </UncontrolledButtonDropdown>
              </Col>
            </FormGroup>
            <FormGroup row>
              <Label for="User" sm={4} className="text-center">
                <h5>User: </h5>
              </Label>
              <Col sm={4}>
                <UncontrolledButtonDropdown>
                  <DropdownToggle caret>
                    {this.state.selectedUser}
                  </DropdownToggle>
                  <DropdownMenu onClick={this.handleUserChange}>
                    {this.state.users.map((user, key) => (
                      <DropdownItem key={key} value={user.id}>
                        {user.firstName} {user.lastName}
                      </DropdownItem>
                    ))}
                  </DropdownMenu>
                </UncontrolledButtonDropdown>
              </Col>
            </FormGroup>
            <FormGroup tag="fieldset" row>
              <div className="row">
                <div className="col-md-4">
                  <legend>
                    <h5 className="pl-4">Book Issue: </h5>
                  </legend>
                </div>
                <div className="col-md-3">
                  <FormGroup check>
                    <Label check>
                      <Input type="radio" name="radio1" /> <h6>Return</h6>
                    </Label>
                  </FormGroup>
                </div>
                <div className="col-md-3">
                  <FormGroup check>
                    <Label check>
                      <Input type="radio" name="radio1" /> <h6> Not Return</h6>
                    </Label>
                  </FormGroup>
                </div>
              </div>
            </FormGroup>
            <FormGroup row>
              <button className="btn btn-outline-success btn-md rounded-0 mx-auto px-5 shadow">
                <h2>SAVE</h2>
              </button>
            </FormGroup>
          </Form>
        </div>
      </div>
    );
  }
}
export default UpdateBookIssue;
