import React, { Component } from 'react';
import {  Form, FormGroup, Label, Input, Col } from 'reactstrap';
import axios from 'axios';



class PostFormOfBook extends React.Component{
    state={
        name:"fe",
        quantity:1,
        location:"f",
        description:"f",
        isbn:"f",
        publisher:"f",
        author:"f",
    };
    handleChange=event=> {
        this.setState({[event.target.name]: event.target.value});
        console.log(this.state)
    };
    handleSubmit=event=>{
        event.preventDefault();
        const book={
            name: this.state.name,
            quantity: this.state.quantity,
            location:  this.state.location,
            description: this.state.description,
            isbn: this.state.isbn,
            publisher: this.state.publisher,
            author: this.state.author
        };
        axios({method:"post",url:'http://localhost:8080/api/V1/book',data:book,headers:{'Content-Type':'application/json', "Access-Control-Allow-Origin": "*"}})
            .then(res=>{
                console.log(res);
                console.log(res.data)
            }).catch(error=>{
                console.log(error);
        });
    };


    render() {
        return (
            <div className="container">
            <div className="col-md-10 d-flex justify-content-center">
            <Form onSubmit={this.handleSubmit} className="mt-4 border border-light p-5 shadow-lg form-background">
                <h2 className="text-center mb-5">NEW BOOK FORM</h2>
                <FormGroup row>
                    <Label for="exampleEmail" sm={2}>Book name: </Label>
                    <Col sm={10}>
                        <Input type="text" name="name" id="exampleEmail" placeholder="Book Name" onChange={this.handleChange} />
                    </Col>
                </FormGroup>
                <FormGroup row>
                    <Label for="examplePassword" sm={2}>Book location</Label>
                    <Col sm={10}>
                        <Input type="text" name="location" id="examplePassword" placeholder="Book price" onChange={this.handleChange}/>
                    </Col>
                </FormGroup>

                <FormGroup row>
                    <Label for="examplePassword" sm={2}>Book ISBN</Label>
                    <Col sm={10}>

                        <Input type="text" name="isbn" id="examplePassword" placeholder="Book ISBN" onChange={this.handleChange}/>
                    </Col>
                </FormGroup>
                <FormGroup row>
                    <Label for="examplePassword" sm={2}>Book quantity</Label>
                    <Col sm={10}>
                        <Input type="number" name="quantity" id="examplePassword" placeholder="Book quantity" onChange={this.handleChange}/>
                    </Col>
                </FormGroup>
                <FormGroup row>
                    <Label for="examplePassword" sm={2}>Book publisher</Label>
                    <Col sm={10}>

                        <Input type="text" name="publisher" id="examplePassword" placeholder="Book publisher" onChange={this.handleChange}/>
                    </Col>
                </FormGroup>
                <FormGroup  row>
                    <Label for="examplePassword" sm={2}>Book author</Label>
                    <Col sm={10}>
                        <Input type="text" name="author" id="examplePassword" placeholder="Book author" onChange={this.handleChange}/>
                    </Col>
                </FormGroup>
                <FormGroup row>
                    <Label for="exampleText" sm={2}>Book description</Label>
                    <Col sm={10}>
                        <Input type="textarea" name="description" id="exampleText" onChange={this.handleChange}/>
                    </Col>
                </FormGroup>
                <FormGroup  row>

                        <button className="btn btn-outline-success btn-lg rounded-0 mx-auto px-4">SAVE</button>

                </FormGroup>
            </Form>
            </div>
            </div>
        );
    }
}
export default PostFormOfBook;
